<template>
    <div v-if="node.type === 'text'" class="text dashed noDrage" :style="textStyle(node)" style="width: 100%;height: 100%;position: flex;align-content: center;word-break: break-all;">{{ node.title }}</div>
    <div v-if=" node.type === 'image'" class="dashed noDrage" :style="imageStyle(node)" style="width: 100%; height: 100%;display: flex;justify-content: center;align-items: center;">
        <img :src="node.pic" style="width: auto;height: auto;max-width: 100%;max-height: 100%;-webkit-user-drag: none;">
    </div>
    <div v-if="node.type === 'lineH'" class="lineH noDrage" :class="{'lineHFocus':focusId === node.nodeId || groupNodeIds.includes(node.nodeId)}" :style="lineHStyle(node)"></div>
    <div v-if="node.type === 'lineV'" class="lineV noDrage" :class="{'lineHFocus':focusId === node.nodeId || groupNodeIds.includes(node.nodeId)}" :style="lineVStyle(node)"></div>
    <div v-if="node.type === 'rect'" class="rect noDrage" :style="rectStyle(node)">{{ node.title }}</div>
    <div v-if="node.type === 'cricle'" class="cricle noDrage" :style="cricleStyle(node)">{{ node.title }}</div>
    <div v-if="node.type === 'QRCode'" class="QRCode dashed noDrage" :style="QRCodeStyle(node)" style="display: flex;justify-content: center;align-items: center;">
        <img :id="node?.title" :src="qrCodeBase64"  style="width: auto;height: auto;max-width: 100%;max-height: 100%;-webkit-user-drag: none;">
    </div>
    <div v-if="node.type === 'BarCode'" class="BarCode dashed noDrage" :style="BarCodeStyle(node)" style="display: flex;justify-content: center;align-items: center;">
        <img :id="node?.title" :src="barCodeBase64" style="width: auto;height: auto;max-width: 100%;max-height: 100%;-webkit-user-drag: none;">
    </div>
</template>
<script setup>
import { defineProps } from 'vue';
const props = defineProps({
    node:Object,
    focusId:String,
    groupNodeIds:Array
})
const textStyle = (node) => {
    return {
        border: node.isBorder ? `${node.borderWidth}px solid ${node.borderColor}` : "",
        borderRadius: `${node.borderRadius}px`,
        padding: `${node.nodePadding}px`,
        textAlign:`${node.fontTextAlign}`,
        textAlignLast:`${node.fontTextAlign}`,
        lineHeight:`${node.fontLineHeight}px`
    }
}
const imageStyle = (node) => {
    return {
        border: node.isBorder ? `${node.borderWidth}px solid ${node.borderColor}` : "",
        borderRadius: `${node.borderRadius}px`,
        padding: `${node.nodePadding}px`,
    };
}

const lineHStyle = (node) => {
    return {
        width:'100%',
        borderTop:`${node.borderWidth}px solid ${node.borderColor}`
    }
}
const lineVStyle = (node) => {
    return {
        height:'100%',
        borderLeft:`${node.borderWidth}px solid ${node.borderColor}`
    }
}
const rectStyle = (node) => {
    return {
        width:'100%',
        height:'100%',
        wordbreak: 'break-all',
        display:'flex',
        justifyContent: 'center',
        alignItems: 'center',
        border: node.isBorder ? `${node.borderWidth}px solid ${node.borderColor}` : "",
        borderRadius: `${node.borderRadius}px`,
        padding: `${node.nodePadding}px`,
        backgroundColor:`${node.bgColor}`
    }
}
const cricleStyle = (node) => {
    return {
        width:'100%',
        height:'100%',
        wordBreak: 'break-all',
        display:'flex',
        justifyContent: 'center',
        alignItems: 'center',
        border: node.isBorder ? `${node.borderWidth}px solid ${node.borderColor}` : "",
        borderRadius: `100%`,
        padding: `${node.nodePadding}px`,
    }
}
const QRCodeStyle = (node) => {
    return {
        width:'100%',
        height:'100%',
        border: node.isBorder ? `${node.borderWidth}px solid ${node.borderColor}` : "",
        borderRadius: `${node.borderRadius}px`,
        padding: `${node.nodePadding}px`,
    }
}
const BarCodeStyle = (node) => {
    return {
        width:'100%',
        height:'100%',
        border: node.isBorder ? `${node.borderWidth}px solid ${node.borderColor}` : "",
        borderRadius: `${node.borderRadius}px`,
        padding: `${node.nodePadding}px`,
    }
}
const qrCodeBase64 = 'data:image/jpeg;base64,'
const barCodeBase64 = 'data:image/jpeg;base64,'
</script>
<style scoped lang="less">
.dashed{
    border: 1px dashed #bbb;
}
.noDrage{
    -webkit-user-drag: none;
}
.text{
    user-select: none;
}
.lineH{
    padding: 3px 0;
    position: absolute;
    top: 0;
}
.lineHFocus{
    background-color: rgba(0, 255, 0, 0.3) !important;
}
.lineV{
    padding: 0 3px;
    position: absolute;
    left: 0;
}
</style>
